import { Component } from '@angular/core';
import { ProductService } from '../product.service';
import { OrderService } from '../order.service';
import { Location } from '@angular/common';
import { User } from '../user';

@Component({
  selector: 'app-order',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.css']
})
export class OrderComponent {
  email: string = '';
  password: string = '';
  firstName: string = '';
  lastName: string = '';
  adresse: string = '';
  user: User | undefined;

  constructor(private productService: ProductService, private orderService: OrderService,
    private location: Location, private route: ActivatedRoute,

  ) {
  }

  validateOrder(firstName: string, lastName: string, email: string, password: string, adresse: string): void {

    //alert(adresse);
    const slug = this.route.snapshot.paramMap.get('slug');

    this.productService.registerTo(email, password, firstName, lastName)
      .subscribe((user) => {
        this.user = user;
        console.log(user);
        if (user.token != undefined) {
          this.orderService.validateOrder(email, user.token, adresse)
            .subscribe((user) => {
              this.user = user;
              console.log(user);
            });
        } else {
          this.location.back();
        }
      });
  }

}
